// src/ImageUploader.js (React frontend)
import React, { useState } from 'react';

const ImageUploader = () => {
  const [imagePreview, setImagePreview] = useState('');
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState('');

  const handleImageChange = (e) => {
    const file = e.target.files[0];

    if (file) {
      const reader = new FileReader();

      reader.onload = (readerEvent) => {
        setImagePreview(readerEvent.target.result);
      };

      reader.readAsDataURL(file);
    }
  };

  const handlePredictClick = async () => {
    const formData = new FormData();
    const fileInput = document.getElementById('imageUpload');

    if (fileInput.files.length > 0) {
      formData.append('file', fileInput.files[0]);

      // Show loading animation
      setLoading(true);

      try {
        const response = await fetch('http://127.0.0.1:5000/predict', {
          method: 'POST',
          body: formData,
        });

        const data = await response.json();
        setResult('Result: ' + data.result);
        setLoading(false);
        console.log('Success!');
      } catch (error) {
        console.error('Error:', error);
        setLoading(false);
      }
    }
  };

  return (
    <div>
      <div className="image-section" style={{ display: imagePreview ? 'block' : 'none' }}>
        <img src={imagePreview} alt="Preview" style={{ maxWidth: '100%', maxHeight: '200px' }} />
      </div>
      <div className="loader" style={{ display: loading ? 'block' : 'none' }}>
        {/* Render your loading animation here */}
      </div>
      <div id="result" style={{ display: result ? 'block' : 'none' }}>
        {result}
      </div>
      <input type="file" id="imageUpload" onChange={handleImageChange} />
      <button onClick={handlePredictClick}>Predict</button>
    </div>
  );
};

export default ImageUploader;
